package com.example;

import java.util.List;

import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

@RestController
@RequestMapping("/api/articulos")

public class ArticuloController {

	@GetMapping
	public List<Articulo> findAll(){
		System.out.println("findAll");
		
		return List.of(
				new Articulo(1L,"Titulo1", "https://www.ocu.org/-/media/ocu/images/home/tecnologia/ordenador/portatiles_800x450.jpg?rev=03bce074-b59f-412c-b69e-ffc716573606&mw=660&hash=7F6D216332DE2B8AFBA7DF6DF6914C2A"),
				new Articulo(2L,"Titulo2", "data:image/jpeg;base64,"),
				new Articulo(3L, "Titulo3", "https://www.ocu.org/-/media/ocu/images/home/tecnologia/ordenador/sobremesa_600.jpg?rev=529ba3f8-5ec3-4bd3-99ae-d189bcbd976a&h=400&w=600&hash=B2A9570DB13D4420DDB5C0221F39DE80&mw=960	")
				);
		
	}
	
	@GetMapping("/{id}")
	public Articulo findById(@PathVariable Long id) {
		System.out.println("findByID" + id);
		
		return new Articulo(id,"Titulo de ejemplo","");
	}
}
